package cn.com.amber.configuration;import org.redisson.Redisson;import org.redisson.api.RedissonClient;import org.redisson.config.Config;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.core.io.ClassPathResource;import java.io.IOException;/** * TODO:class description * * @author yangying * @version 1.0 * @since 2022/9/14 **/@Configurationpublic class RedissonConfig {    @Bean    public RedissonClient redissonClient() {        Config config = null;        try {            config = Config.fromYAML(new ClassPathResource("redisson-cluster.yml").getInputStream());        } catch (IOException e) {            throw new RuntimeException(e);        }        RedissonClient redisson = Redisson.create(config);        return redisson;    }}