package cn.com.amber.controller;import cn.com.amber.entity.Account;import cn.com.amber.mapper.AccountMapper;import com.alibaba.fastjson2.JSONObject;import org.springframework.util.StringUtils;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;/** * TODO:class description * * @author yangying * @version 1.0 * @since 2022/9/3 **/@RestController@RequestMapping(path = "/account")public class AccountManagerController {    @Resource    private AccountMapper accountMapper;    @RequestMapping(path = "/getUser/{userId}")    public String getUer(@PathVariable String userId){        if(userId.equals("111")){            return "john";        }        return "has not found";    }    @RequestMapping(path = "/getAccount/{userId}/{accountNo}")    public String getAccount(@PathVariable String accountNo,@PathVariable String userId){        if(StringUtils.isEmpty(accountNo)){            return "get nothing";        }        Account account = accountMapper.selectByAccountNo(userId,accountNo);        return JSONObject.toJSONString(account);    }}