package cn.com.amber.entity;import lombok.Data;import lombok.NoArgsConstructor;import java.math.BigDecimal;import java.sql.Timestamp;import java.util.Date;/** * 账户实体 * * @author yangying * @version 1.0 * @since 2022/9/4 **/@Data@NoArgsConstructorpublic class Account {    private Long id;    private String accountName;    private String accountNo;    private BigDecimal balance;    private BigDecimal availableBalance;    private BigDecimal frozenBalance = new BigDecimal(0.0);    private Date gmtCreated;    private String creator;    private Date gmtModified;    private String modifier;    public Account(Long id, String accountName, String accountNo, BigDecimal balance, BigDecimal availableBalance,BigDecimal frozenBalance,                   String creator,Date gmtCreated, String modifier, Date gmtModified) {        this.id = id;        this.accountName = accountName;        this.accountNo = accountNo;        this.balance = balance;        this.availableBalance = availableBalance;        this.frozenBalance = frozenBalance;        this.gmtCreated = gmtCreated;        this.creator = creator;        this.gmtModified = gmtModified;        this.modifier = modifier;    }}