package cn.com.amber.entity.enums;import lombok.Getter;/** * 账户状态枚举 * * @author yangying * @version 1.0 * @since 9:01 PM **/public enum AccountStatusEnum {    IN_USE(1,"在用"),    FROZEN(2,"冻结"),    DESTROYED(3,"已注销"),;    @Getter    private int code;    @Getter    private String name;    private AccountStatusEnum(int code,String name){        this.code = code;        this.name = name;    }    public static AccountStatusEnum getValue(int code){        for(AccountStatusEnum each:AccountStatusEnum.values()){            if(code == each.code){                return each;            }        }        return null;    }}