package cn.com.amber.entity.enums;import lombok.Getter;/** * 账户类型 * * @author yangying * @version 1.0 * @since 2022/9/29 **/public enum AccountTypeEnum {    SUMMARY(1,"总账"),    DETAIL(2,"分账"),    UN_KNOW(99,"未知");    @Getter    private final int code;    @Getter    private final String name;    private AccountTypeEnum(int code, String name){        this.code = code;        this.name = name;    }    public static AccountTypeEnum getValue(int code){        for(AccountTypeEnum each: AccountTypeEnum.values()){            if(code == each.getCode()){                return each;            }        }        return UN_KNOW;    }}