package cn.com.amber.entity.enums;import lombok.Getter;/** * 会计要素 * * @author yangying * @version 1.0 * @since 2022/9/13 **/public enum AccountingElementEnum {    ASSERT(1, "资产"),    LIABILITIES(2, "负债"),    PROFIT_AND_LOSS(6, "损益");    @Getter    private int code;    @Getter    private String name;    private AccountingElementEnum(int code, String name) {        this.code = code;        this.name = name;    }    public static AccountingElementEnum getValue(int code) {        for (AccountingElementEnum each : AccountingElementEnum.values()) {            if (code == each.code) {                return each;            }        }        return null;    }}