package cn.com.amber.entity.enums;import lombok.Getter;/** * 账户主体类型 * * @author yangying * @version 1.0 * @since 2022/9/21 **/public enum LegalPersonType {    INDIVIDUAL(1,"个人"),    BUSINESS_ASSETS(2,"企业-资产端"),    BUSINESS_FUNDS(3,"企业资金端"),    SELF(4,"自身主体"),    UN_KNOW(99,"未知的");    @Getter    private final int code;    @Getter    private final String name;    private LegalPersonType(int code, String name){        this.code = code;        this.name = name;    }    public static LegalPersonType getValue(int code){        for(LegalPersonType each: LegalPersonType.values()){            if(code == each.getCode()){                return each;            }        }        return UN_KNOW;    }}