package cn.com.amber.events;import cn.com.amber.entity.Account;import com.google.gson.reflect.TypeToken;import lombok.Getter;import java.lang.reflect.Type;/** * 事件类型枚举 * * @author yangying * @version 1.0 * @since 9:01 PM **/@Getterpublic enum EventTypeEnum {    ACCOUNT_CREATED("ACCOUNT_CREATED",new TypeToken<Event<Account>>(){}.getType());    private String name;    private Type type;    private EventTypeEnum(String name,Type type){        this.name = name;        this.type = type;    }    public static EventTypeEnum getValue(String name){        for(EventTypeEnum each:EventTypeEnum.values()){            if(each.getName().equals(name)){                return each;            }        }        return null;    }}