package cn.com.amber.infrastructure.annotation;import java.lang.annotation.*;/** * 标记为ApiLog * * @author yangying * @version 1.0 * @since 10:32 AM **/@Documented@Retention(RetentionPolicy.RUNTIME)@Target({ElementType.METHOD,ElementType.TYPE})public @interface ApiLog {    /**     * STRAND-标准;RESULT-restful     * @return     */    String value() default API_STANDARD;    public static String API_STANDARD = "standard";    public static String API_SIMPLE = "simple";    public static String API_REMOTE = "remote";    public static String API_RESTFUL = "restful";}