package cn.com.amber.infrastructure.exception;import cn.com.amber.infrastructure.api.enums.ResponseCode;import lombok.Getter;/** * 业务异常 * * @author yangying * @version 1.0 * @since 2022/9/24 **/@Getterpublic class BusinessException extends RuntimeException{    private static final long serialVersionUID = 6715130320123450546L;    private final ResponseCode responseCode;    private BusinessException(ResponseCode responseCode, String message){        super(message);        this.responseCode = responseCode;    }    private BusinessException(ResponseCode responseCode, String message, Throwable cause){        super(message,cause);        this.responseCode = responseCode;    }    public static BusinessException create(ResponseCode responseCode){        return new BusinessException(responseCode,responseCode.getMessage());    }    public static BusinessException create(ResponseCode responseCode,Object... args){        String message = String.format(responseCode.getMessage(),args);        return new BusinessException(responseCode,message);    }    public static BusinessException create(ResponseCode responseCode,Throwable cause){        return new BusinessException(responseCode,responseCode.getMessage(),cause);    }    public static BusinessException create(ResponseCode responseCode,Throwable cause ,Object... args){        String message = String.format(responseCode.getMessage(),args);        return new BusinessException(responseCode,message,cause);    }}