package cn.com.amber.service;import cn.com.amber.entity.Subject;import cn.com.amber.mapper.SubjectMapper;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;/** * 科目服务 * * @author yangying * @version 1.0 * @since 2022/9/21 **/@Servicepublic class SubjectService {    @Resource    private SubjectMapper subjectMapper;    public List<Subject> querySubjects(List<String> subjectNos){        return subjectMapper.selectBySubjectNoList(subjectNos);    }}